    id=#{orderNo}

mapper.select:
select
from delivery_order a left join ( select * from curr_order_receiver a1 left join (select b1.id as id1,b1.plate_number as plate_number, b2.type_desc as type_desc from vehicle b1,truck_type b2 where b1.truck_type_id=b2.id )a2 on a1.vehicle_id=a2.id1 ) f on a.id=f.delivery_order_id left join vegetable_delivery_relation b on a.id=b.delivery_order_id left join vehicle_gps_record c on a.id=c.delivery_order_id left join vegetable_certificate d on a.id=d.delivery_order_id left join vegetable_image e on a.id=e.delivery_order_id
where a.id = #{deliveryOrderId}

