mapper.select:
    select

from quality_sheet a  left join  delivery_order b on  a.delivery_order_id=b.id
    where
    (a.user_id=#{useId}
    or

( b.receiver_online_user_id=#{useId}
or b.driver_online_user_id=#{useId}
or b.creator_id=#{useId}

))

  and( a.category_name like  concat("%",#{searchName},"%")
  or a.code like concat("%",#{searchName},"%")
  )
  and a.is_delete=0
   order by gmt_create desc
